import api from './api';

const WalletService = {
  // 지갑 잔액 조회
  getBalance: async () => {
    try {
      const response = await api.get('/api/wallet/balance');
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // 입금 주소 조회
  getDepositAddress: async (currency) => {
    try {
      const response = await api.get(`/api/wallet/deposit/${currency}`);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // 출금 요청
  requestWithdrawal: async (withdrawalData) => {
    try {
      const response = await api.post('/api/wallet/withdraw', withdrawalData);
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // 입출금 내역 조회
  getTransactions: async (params) => {
    try {
      const response = await api.get('/api/wallet/transactions', { params });
      return response.data;
    } catch (error) {
      throw error;
    }
  }
};

export default WalletService;
