import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import WalletService from '../services/WalletService';
import './WalletPage.css';

const WalletPage = () => {
  const { user, isAuthenticated } = useAuth();
  const [balance, setBalance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBalance = async () => {
      if (!isAuthenticated) {
        setError('로그인이 필요합니다.');
        setLoading(false);
        return;
      }

      try {
        const data = await WalletService.getBalance();
        setBalance(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBalance();
  }, [isAuthenticated]);

  if (loading) return <div className="wallet-loading">로딩 중...</div>;
  if (error) return <div className="wallet-error">에러: {error}</div>;
  if (!isAuthenticated) return <div className="wallet-error">로그인이 필요합니다.</div>;

  return (
    <div className="wallet-page">
      <h1>내 지갑</h1>
      <div className="wallet-balance">
        <h2>보유 자산</h2>
        <div className="balance-list">
          {balance && Object.entries(balance).map(([currency, amount]) => (
            <div key={currency} className="balance-item">
              <span className="currency">{currency}</span>
              <span className="amount">{amount.toLocaleString()}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default WalletPage;
