// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import TradingLayout from './components/layout/TradingLayout';
import BalancePage from './pages/BalancePage';
import TradingPage from './pages/TradingPage';
import InvestPage from './pages/InvestPage';
import DepositPage from './pages/DepositPage';
import WithdrawPage from './pages/WithdrawPage';
import WalletPage from './pages/WalletPage';
import { MarketProvider } from './context/MarketContext';
import { OrderProvider } from './context/OrderContext';
import { CryptoDataProvider } from './context/CryptoDataContext';
import { AuthProvider } from './context/AuthContext';
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import './styles/App.css';

function App() {
  return (
    <AuthProvider>
      <MarketProvider>
        <OrderProvider>
          <CryptoDataProvider>
            <Router>
              <div className="app">
                <Navbar />
                <main className="main-content">
                  <Routes>
                    <Route path="/" element={<HomePage />} />
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/wallet" element={<WalletPage />} />
                    <Route path="/" element={<TradingLayout />}>
                      <Route index element={<TradingPage />} />
                      <Route path="/exchange" element={<TradingPage />} />
                      <Route path="/trading/:symbol" element={<TradingPage />} />
                      <Route path="/investments/balance" element={<BalancePage />} />
                      <Route path="/investments/area" element={<InvestPage />} />
                      <Route path="/deposit" element={<DepositPage />} />
                      <Route path="/withdraw" element={<WithdrawPage />} />
                      <Route path="/wallet" element={<WalletPage />} />
                    </Route>
                  </Routes>
                </main>
              </div>
            </Router>
          </CryptoDataProvider>
        </OrderProvider>
      </MarketProvider>
    </AuthProvider>
  );
}

export default App;
